  --accent: hsl(210, 40%, 96%);
  --accent-foreground: hsl(222, 84%, 4.9%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --ring: hsl(207, 90%, 54%);
  --radius: 0.5rem;

  /* Taskego Warm Professional Colors */
  --taskego-primary: hsl(211, 85%, 45%); /* Warm Professional Blue */
  --taskego-secondary: hsl(220, 15%, 65%); /* Warm Gray */
  --taskego-accent: hsl(200, 50%, 55%); /* Soft Blue */
  --taskego-neutral: hsl(220, 10%, 88%); /* Warm Light Gray */
  --taskego-dark: hsl(215, 25%, 20%); /* Warm Dark Gray */
  --taskego-muted: hsl(210, 15%, 70%); /* Muted Blue-Gray */
  --taskego-subtle: hsl(205, 20%, 75%); /* Subtle Blue */
  --taskego-contrast: hsl(215, 40%, 35%); /* Contrast Blue */
}

.dark {
  --background: hsl(222, 84%, 4.9%);
  --foreground: hsl(210, 40%, 98%);
  --muted: hsl(217, 32%, 17%);
  --muted-foreground: hsl(215, 16%, 46.9%);
  --popover: hsl(222, 84%, 4.9%);
  --popover-foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 84%, 4.9%);
  --card-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 32%, 17%);
  --input: hsl(217, 32%, 17%);
  --primary: hsl(211, 100%, 43%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(217, 32%, 17%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --accent: hsl(217, 32%, 17%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 62%, 30%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --ring: hsl(207, 90%, 54%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
  }

  /* 3D Perspective for Card Flips */
  .perspective-1000 {
    perspective: 1000px;
  }

  .backface-hidden {
    backface-visibility: hidden;
  }

  .preserve-3d {
    transform-style: preserve-3d;
  }

  /* RTL Support */
  [dir="rtl"] {
    direction: rtl;
  }

  [dir="rtl"] .text-left {
    text-align: right;
  }

  [dir="rtl"] .text-right {
    text-align: left;
  }

  [dir="rtl"] .ml-auto {
    margin-left: 0;
    margin-right: auto;
  }

  [dir="rtl"] .mr-auto {
    margin-right: 0;
    margin-left: auto;
  }
}

@layer components {
  /* Bold High-Contrast Khadamati Theme */
  .khadamati-blue {
    @apply text-[#1e40af];
  }

  .bg-khadamati-blue {
    @apply bg-[#1e40af];
  }

  .khadamati-yellow {
    @apply text-[#f59e0b];
  }

  .bg-khadamati-yellow {
    @apply bg-[#f59e0b];
  }

  .khadamati-success {
    @apply text-[hsl(142,76%,36%)];
  }

  .bg-khadamati-success {
    @apply bg-[hsl(142,76%,36%)];
  }

  .khadamati-error {
    @apply text-[hsl(0,84%,60%)];
  }

  .bg-khadamati-error {
    @apply bg-[hsl(0,84%,60%)];
  }

  .khadamati-light {
    @apply text-[hsl(210,40%,98%)];
  }

  .bg-khadamati-light {
    @apply bg-[hsl(210,40%,98%)];
  }

  .khadamati-gray {
    @apply text-[hsl(215,16%,46.9%)];
  }

  .bg-khadamati-gray {
    @apply bg-[hsl(215,16%,46.9%)];
  }

  .khadamati-dark {
    @apply text-[hsl(220,30%,15%)];
  }

  .bg-khadamati-dark {
    @apply bg-[hsl(220,30%,15%)];
  }

  /* Gradient Text - Fixed for better visibility */
  .text-gradient {
    background: linear-gradient(135deg, hsl(220, 85%, 45%) 0%, hsl(42, 100%, 45%) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    font-weight: 700;
  }

  /* Ultra High Contrast Text Classes */
  .text-yellow-bright {
    @apply text-[#d97706] font-bold;
  }

  .bg-yellow-bright {
    @apply bg-[#f59e0b];
  }

  .border-yellow-bright {
    @apply border-[#f59e0b];
  }

  /* Super Dark text for maximum readability */
  .text-dark-readable {
    @apply text-[#111827] font-semibold;
  }

  /* White text for dark backgrounds */
  .text-white-clean {
    @apply text-white font-semibold;
  }

  /* Floating Animation */
  .floating {
    animation: floating 3s ease-in-out infinite;
  }

  @keyframes floating {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  /* Glow Effect */
  .glow-blue {
    box-shadow: 0 0 20px hsla(207, 90%, 54%, 0.3);
  }

  /* Card Hover Effects */
  .card-hover {
    transition: all 0.3s ease;
  }

  .card-hover:hover {
    transform: translateY(-8px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
  }

  /* Button Fixes for Better Contrast */
  .btn-primary {
    background: linear-gradient(135deg, hsl(220, 85%, 45%) 0%, hsl(42, 100%, 50%) 100%) !important;
    color: white !important;
    border: none !important;
    font-weight: 600 !important;
  }

  .btn-primary:hover {
    background: linear-gradient(135deg, hsl(220, 85%, 35%) 0%, hsl(42, 100%, 40%) 100%) !important;
    transform: scale(1.05);
  }

  /* Text shadow for better readability */
  .text-shadow-lg {
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
  }

  .glow-yellow {
    box-shadow: 0 0 20px hsla(37, 91%, 55%, 0.3);
  }

  /* Pulse Animation */
  .pulse-slow {
    animation: pulse-slow 4s ease-in-out infinite;
  }

  @keyframes pulse-slow {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.7; }
  }

  /* Scroll Reveal Animation */
  .scroll-reveal {
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.6s cubic-bezier(0.23, 1, 0.32, 1);
  }

  .scroll-reveal.visible {
    opacity: 1;
    transform: translateY(0);
  }

  /* Service Card Hover Effects */
  .service-card {
    transition: all 0.3s cubic-bezier(0.23, 1, 0.32, 1);
    transform-style: preserve-3d;
  }

  .service-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  }

  .service-card-flip {
    transition: transform 0.6s;
    transform-style: preserve-3d;
  }

  .service-card-flip:hover {
    transform: rotateY(180deg);
  }

  .service-card-front,
  .service-card-back {
    backface-visibility: hidden;
  }

  .service-card-back {
    transform: rotateY(180deg);
  }

  /* Parallax Container */
  .parallax {
    transform-style: preserve-3d;
  }

  /* Glass Morphism */
  .glass {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  .glass-dark {
    background: rgba(0, 0, 0, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  /* Interactive Buttons */
  .btn-animated {
    position: relative;
    overflow: hidden;
    transition: all 0.3s ease;
  }

  .btn-animated::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.5s;
  }

  .btn-animated:hover::before {
    left: 100%;
  }

  /* Hero Section Animations */
  .hero-bg {
    background: linear-gradient(135deg, 
      hsla(207, 90%, 54%, 0.1) 0%, 
      hsla(37, 91%, 55%, 0.1) 100%);
    position: relative;
    overflow: hidden;
  }

  .hero-bg::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23000000' fill-opacity='0.03'%3E%3Ccircle cx='30' cy='30' r='2'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E");
    opacity: 0.5;
  }

  /* Chat Widget Animations */
  .chat-bubble {
    animation: chat-bounce 0.5s cubic-bezier(0.68, -0.55, 0.265, 1.55);
  }

  @keyframes chat-bounce {
    0% { transform: scale(0) rotate(45deg); }
    50% { transform: scale(1.1) rotate(22.5deg); }
    100% { transform: scale(1) rotate(0deg); }
  }

  /* Loading Animations */
  .spinner {
    animation: spin 1s linear infinite;
  }

  @keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  .bounce-dots > div {
    animation: bounce-dots 1.4s ease-in-out infinite both;
  }

  .bounce-dots > div:nth-child(1) { animation-delay: -0.32s; }
  .bounce-dots > div:nth-child(2) { animation-delay: -0.16s; }

  @keyframes bounce-dots {
    0%, 80%, 100% { 
      transform: scale(0);
    } 40% { 
      transform: scale(1);
    }
  }

  /* Scroll Reveal */
  .scroll-reveal {
    opacity: 0;
    transform: translateY(50px);
    transition: all 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  }

  .scroll-reveal.revealed {
    opacity: 1;
    transform: translateY(0);
  }

  /* Page Transitions */
  .page-transition {
    opacity: 0;
    animation: fadeIn 0.6s ease-out forwards;
  }

  @keyframes fadeIn {
    to {
      opacity: 1;
    }
  }

  /* Mobile Navigation */
  .nav-mobile {
    transform: translateX(-100%);
    transition: transform 0.3s ease-in-out;
  }

  .nav-mobile.open {
    transform: translateX(0);
  }

  [dir="rtl"] .nav-mobile {
    transform: translateX(100%);
  }

  [dir="rtl"] .nav-mobile.open {
    transform: translateX(0);
  }

  /* Custom Scrollbar */
  .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    background: hsl(210, 40%, 98%);
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    background: hsl(207, 90%, 54%);
    border-radius: 3px;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: hsl(207, 90%, 44%);
  }
}

@layer utilities {
  /* Animation Delays */
  .animation-delay-200 {
    animation-delay: 200ms;
  }

  .animation-delay-400 {
    animation-delay: 400ms;
  }

  .animation-delay-600 {
    animation-delay: 600ms;
  }

  .animation-delay-800 {
    animation-delay: 800ms;
  }

  /* Arabic Font Support */
  .font-arabic {
    font-family: 'Noto Sans Arabic', 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
  }

  /* Responsive Text Sizes */
  .text-responsive-sm {
    @apply text-sm md:text-base;
  }

  .text-responsive-base {
    @apply text-base md:text-lg;
  }

  .text-responsive-lg {
    @apply text-lg md:text-xl;
  }

  .text-responsive-xl {
    @apply text-xl md:text-2xl;
  }

  .text-responsive-2xl {
    @apply text-2xl md:text-3xl;
  }

  .text-responsive-3xl {
    @apply text-3xl md:text-4xl;
  }
}

