import { useState } from 'react';
import { Globe, ChevronDown } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from '@/components/ui/dropdown-menu';

interface LanguageSwitcherProps {
  currentLanguage: string;
  onLanguageChange: (lang: 'en' | 'ar') => void;
  messages: any;
}

export default function LanguageSwitcher({ currentLanguage, onLanguageChange, messages }: LanguageSwitcherProps) {
  const languages = [
    { code: 'en', label: 'English', nativeLabel: 'English' },
    { code: 'ar', label: 'Arabic', nativeLabel: 'Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©' },
  ];

  const currentLang = languages.find(lang => lang.code === currentLanguage) || languages[0];

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="ghost" className="flex items-center space-x-2 px-3 py-2">
          <Globe className="h-4 w-4 text-khadamati-gray" />
          <span className="text-sm font-medium">{currentLang.code.toUpperCase()}</span>
          <ChevronDown className="h-3 w-3 text-khadamati-gray" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-32">
        {languages.map((lang) => (
          <DropdownMenuItem
            key={lang.code}
            onClick={() => onLanguageChange(lang.code as 'en' | 'ar')}
            className={`cursor-pointer ${
              currentLanguage === lang.code ? 'bg-khadamati-light' : ''
            }`}
          >
            <span className="text-sm">{lang.nativeLabel}</span>
          </DropdownMenuItem>
        ))}
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

client/src/components/common/ParallaxHer.content:
import React, { useEffect, useRef } from 'react';
import { motion, useScroll, useTransform, useSpring } from 'framer-motion';
import { ArrowDown, Play, Star, Users, CheckCircle, TrendingUp } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent } from '@/components/ui/card';
import ScrollReveal from './ScrollReveal';
import type { Messages } from '@/lib/i18n';

interface ParallaxHeroProps {
  messages: Messages;
  onGetStarted: () => void;
  onWatchDemo: () => void;
}

export default function ParallaxHero({ messages, onGetStarted, onWatchDemo }: ParallaxHeroProps) {
  const ref = useRef<HTMLDivElement>(null);
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start start", "end start"]
  });

  // Parallax transforms
  const y1 = useTransform(scrollYProgress, [0, 1], [0, -50]);
  const y2 = useTransform(scrollYProgress, [0, 1], [0, -100]);
  const y3 = useTransform(scrollYProgress, [0, 1], [0, -150]);
  const opacity = useTransform(scrollYProgress, [0, 0.5], [1, 0]);
  const scale = useTransform(scrollYProgress, [0, 0.5], [1, 0.8]);

  // Smooth spring animations
  const springConfig = { stiffness: 100, damping: 30, restDelta: 0.001 };
  const y1Spring = useSpring(y1, springConfig);
  const y2Spring = useSpring(y2, springConfig);
  const y3Spring = useSpring(y3, springConfig);

  const stats = [
    { icon: Users, label: 'Active Users', value: '50K+', color: 'text-khadamati-blue' },
    { icon: CheckCircle, label: 'Services Completed', value: '125K+', color: 'text-khadamati-success' },
    { icon: Star, label: 'Average Rating', value: '4.9/5', color: 'text-khadamati-yellow' },
    { icon: TrendingUp, label: 'Growth Rate', value: '+120%', color: 'text-khadamati-blue' }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
        delayChildren: 0.3
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: {
        duration: 0.6,
        ease: [0.25, 0.46, 0.45, 0.94]
      }
    }
  };

  const floatingVariants = {
    initial: { y: 0 },
    animate: {
      y: [-10, 10, -10],
      transition: {
        duration: 6,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  return (
    <div ref={ref} className="relative min-h-screen overflow-hidden bg-gradient-to-br from-white via-blue-50 to-purple-50">
      {/* Background Elements */}
      <div className="absolute inset-0">
        {/* Animated Gradient Orbs */}
        <motion.div
          style={{ y: y3Spring }}
          className="absolute top-20 left-10 w-72 h-72 bg-gradient-to-r from-khadamati-blue/20 to-khadamati-yellow/20 rounded-full blur-3xl"
          animate={{
            scale: [1, 1.2, 1],
            rotate: [0, 180, 360]
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            ease: "linear"
          }}
        />
        <motion.div
          style={{ y: y2Spring }}
          className="absolute bottom-32 right-16 w-96 h-96 bg-gradient-to-r from-khadamati-yellow/15 to-khadamati-blue/15 rounded-full blur-3xl"
          animate={{
            scale: [1.2, 1, 1.2],
            rotate: [360, 180, 0]
          }}
          transition={{
            duration: 25,
            repeat: Infinity,
            ease: "linear"
          }}
        />

        {/* Geometric Patterns */}
        <motion.div
          style={{ y: y1Spring }}
          className="absolute top-1/4 right-1/4 w-64 h-64 opacity-5"
        >
          <svg viewBox="0 0 200 200" className="w-full h-full">
            <motion.polygon
              points="100,10 40,198 190,78 10,78 160,198"
              fill="currentColor"
              className="text-khadamati-blue"
              animate={{ rotate: [0, 360] }}
              transition={{ duration: 30, repeat: Infinity, ease: "linear" }}
            />
          </svg>
        </motion.div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 container mx-auto px-4 min-h-screen flex items-center">
        <div className="grid lg:grid-cols-2 gap-12 items-center w-full">
          {/* Left Column - Content */}
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate="visible"
            style={{ opacity, scale }}
            className="text-center lg:text-left"
          >
            {/* Badge */}
            <motion.div variants={itemVariants} className="mb-6">
              <Badge className="bg-blue-600 text-white px-6 py-3 text-lg font-bold shadow-xl border-2 border-blue-700">
                ðŸš€ #1 Service Platform in the Region
              </Badge>
            </motion.div>

            {/* Main Heading */}
            <motion.h1 variants={itemVariants} className="text-4xl md:text-5xl lg:text-6xl font-black mb-6 leading-tight">
              <span className="text-blue-800 drop-shadow-lg">Taskego</span>
              <br />
              <span className="text-gray-900 font-black">
                Your Trusted Service
              </span>
              <br />
              <span className="text-blue-800 font-black">Marketplace</span>
            </motion.h1>

            {/* Subheading */}
            <motion.p variants={itemVariants} className="text-lg md:text-xl text-gray-800 mb-8 leading-relaxed max-w-2xl font-bold">
              Connect with verified professionals for 
              <span className="text-blue-700 font-black"> cleaning</span>,
              <span className="text-slate-600 font-black"> repairs</span>, and
              <span className="text-blue-700 font-black"> maintenance</span> services. 
              Available in English and Arabic.
            </motion.p>

            {/* CTA Buttons */}
            <motion.div variants={itemVariants} className="flex flex-col sm:flex-row gap-4 mb-12">
              <Button
                onClick={onGetStarted}
                size="lg"
                className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-4 text-xl font-bold shadow-xl border-2 border-blue-700 hover:scale-105 transition-all duration-300"
              >
                Get Started Free
                <ArrowDown className="w-5 h-5 ml-2" />
              </Button>

              <Button
                onClick={onWatchDemo}
                variant="outline"
                size="lg"
                className="border-3 border-blue-700 text-blue-700 hover:bg-blue-700 hover:text-white px-8 py-4 text-xl font-bold shadow-lg transition-all duration-300"
              >
                <Play className="w-5 h-5 mr-2" />
                Watch Demo
              </Button>
            </motion.div>

            {/* Stats */}
            <motion.div variants={itemVariants} className="grid grid-cols-2 lg:grid-cols-4 gap-6">
