const Service = require('../models/Service');
const User = require('../models/User');
const { getGeminiModel, fallbackNLP, isGeminiAvailable } = require('../config/ai');

// Get all services with filtering and search
const getServices = async (req, res) => {
  try {
    const { 
      page = 1, 
      limit = 12, 
      category, 
      subcategory,
      minPrice, 
      maxPrice, 
      city, 
      search,
      sortBy = 'newest',
      language = 'en'
    } = req.query;

    const query = { 
      status: 'approved', 
      isActive: true 
    };

    // Apply filters
    if (category) query.category = category;
    if (subcategory) query.subcategory = new RegExp(subcategory, 'i');
    if (city) query['location.city'] = new RegExp(city, 'i');
    if (minPrice || maxPrice) {
      query.price = {};
      if (minPrice) query.price.$gte = parseFloat(minPrice);
      if (maxPrice) query.price.$lte = parseFloat(maxPrice);
    }

    // Text search
    if (search) {
      query.$or = [
        { title: new RegExp(search, 'i') },
        { description: new RegExp(search, 'i') },
        { tags: new RegExp(search, 'i') }
      ];

      // Add Arabic search if available
      if (language === 'ar') {
        query.$or.push(
          { title_ar: new RegExp(search, 'i') },
          { description_ar: new RegExp(search, 'i') }
        );
      }
    }

    // Sorting
    let sort = {};
    switch (sortBy) {
      case 'price_low':
        sort.price = 1;
        break;
      case 'price_high':
        sort.price = -1;
        break;
      case 'rating':
        sort['rating.average'] = -1;
        break;
      case 'popular':
        sort.bookingCount = -1;
        break;
      default:
        sort.createdAt = -1;
    }

    const services = await Service.find(query)
      .populate('providerId', 'name location rating')
      .sort(sort)
      .skip((page - 1) * limit)
      .limit(parseInt(limit));

    const totalCount = await Service.countDocuments(query);

    // Localize service data based on language
    const localizedServices = services.map(service => ({
      ...service.toObject(),
      title: language === 'ar' && service.title_ar ? service.title_ar : service.title,
      description: language === 'ar' && service.description_ar ? service.description_ar : service.description
    }));

    res.json({
      services: localizedServices,
      pagination: {
        currentPage: parseInt(page),
        totalPages: Math.ceil(totalCount / limit),
        totalCount,
        hasMore: page * limit < totalCount
      },
      filters: {
        category,
        subcategory,
        minPrice,
        maxPrice,  
        city,
        search,
        sortBy,
        language
      }
    });

  } catch (error) {
    console.error('Get services error:', error);
    res.status(500).json({
      message: 'Failed to get services',
      error: error.message
    });
  }
};

