const jwt = require('jsonwebtoken');
const User = require('../models/User');

const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key-here';

// Generate JWT token
const generateToken = (userId) => {
  return jwt.sign({ userId }, JWT_SECRET, { expiresIn: '7d' });
};

// Verify JWT token middleware
const auth = async (req, res, next) => {
  try {
    const token = req.header('Authorization')?.replace('Bearer ', '');

    if (!token) {
      return res.status(401).json({ message: 'Access denied. No token provided.' });
    }

    const decoded = jwt.verify(token, JWT_SECRET);
    const user = await User.findById(decoded.userId);

    if (!user) {
      return res.status(401).json({ message: 'Invalid token. User not found.' });
    }

    if (user.isBlocked) {
      return res.status(403).json({ message: 'Account is blocked. Contact support.' });
    }

    req.user = user;
    next();
  } catch (error) {
    if (error.name === 'TokenExpiredError') {
      return res.status(401).json({ message: 'Token expired. Please login again.' });
    }
    if (error.name === 'JsonWebTokenError') {
      return res.status(401).json({ message: 'Invalid token.' });
    }

    console.error('Auth middleware error:', error);
    res.status(500).json({ message: 'Server error during authentication.' });
  }
};

// Role-based authorization middleware
const authorize = (...roles) => {
  return (req, res, next) => {
    if (!req.user) {
      return res.status(401).json({ message: 'Authentication required.' });
    }

    if (!roles.includes(req.user.role)) {
      return res.status(403).json({ 
        message: `Access denied. Required role: ${roles.join(' or ')}` 
      });
    }

    next();
  };
};

// Optional auth - doesn't fail if token is missing
const optionalAuth = async (req, res, next) => {
  try {
    const token = req.header('Authorization')?.replace('Bearer ', '');

    if (token) {
      const decoded = jwt.verify(token, JWT_SECRET);
      const user = await User.findById(decoded.userId);

      if (user && !user.isBlocked) {
        req.user = user;
      }
    }
  } catch (error) {
    // Silently fail for optional auth
    console.log('Optional auth failed:', error.message);
  }

  next();
};

// Verification status check
const requireVerification = (req, res, next) => {
  if (!req.user.isVerified) {
    return res.status(403).json({ 
      message: 'Email verification required. Please verify your email address.' 
    });
  }
  next();
};

module.exports = {
  generateToken,
  auth,
  authorize,
  optionalAuth,
  requireVerification
};

server/models/Booking.js.content:
const mongoose = require('mongoose');

const bookingSchema = new mongoose.Schema({
  clientId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true
  },
  providerId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true
  },
  serviceId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Service',
    required: true
  },
  date: {
    type: Date,
    required: true
  },
  time: {
    type: String,
    required: true // e.g., '10:00'
  },
  duration: {
    type: Number, // in hours
    default: 1
  },
  status: {
    type: String,
    enum: ['pending', 'accepted', 'in_progress', 'completed', 'cancelled', 'rejected'],
    default: 'pending'
  },
  totalAmount: {
    type: Number,
    required: true,
    min: 0
  },
  paymentStatus: {
    type: String,
    enum: ['pending', 'paid', 'refunded', 'failed'],
    default: 'pending'
  },
  paymentMethod: {
    type: String,
    enum: ['wallet', 'card', 'cash'],
    default: 'wallet'
  },
  notes: String,
  clientNotes: String,
  providerNotes: String,
  cancellationReason: String,
  cancellationDate: Date,
  completionDate: Date,
  systemLogs: [{
    action: String,
    timestamp: {
      type: Date,
      default: Date.now
    },
    details: String
  }],
  location: {
    address: String,
    coordinates: [Number]
  }
}, {
  timestamps: true
});

// Indexes for efficient queries
bookingSchema.index({ clientId: 1, createdAt: -1 });
bookingSchema.index({ providerId: 1, createdAt: -1 });
bookingSchema.index({ serviceId: 1 });
bookingSchema.index({ status: 1 });
bookingSchema.index({ date: 1, time: 1 });

// Add system log entry
bookingSchema.methods.addSystemLog = function(action, details) {
  this.systemLogs.push({
    action,
    details,
    timestamp: new Date()
  });
};

// Check if booking can be cancelled
bookingSchema.methods.canBeCancelled = function() {
  const now = new Date();
  const bookingDateTime = new Date(this.date);
  const hoursDifference = (bookingDateTime - now) / (1000 * 60 * 60);

  return hoursDifference > 24 && ['pending', 'accepted'].includes(this.status);
};

module.exports = mongoose.model('Booking', bookingSchema);

server/models/Payment.js.content:
const mongoose = require('mongoose');

const paymentMethodSchema = new mongoose.Schema({
  type: {
    type: String,
    enum: ['apple_pay', 'card', 'bank_transfer', 'wallet'],
    required: true
  },
  isDefault: {
    type: Boolean,
    default: false
  },
  // Apple Pay specific fields
  applePayData: {
    merchantIdentifier: String,
    paymentPass: {
      primaryAccountIdentifier: String,
      primaryAccountNumberSuffix: String,
      deviceAccountIdentifier: String,
      deviceAccountNumberSuffix: String
    }
  },
  // Card specific fields
  cardData: {
    last4: String,
    brand: String, // visa, mastercard, amex
    expiryMonth: Number,
    expiryYear: Number,
    fingerprint: String
  },
  // Bank transfer fields
  bankData: {
    accountNumber: String,
    routingNumber: String,
    accountHolderName: String,
    bankName: String
  },
  isActive: {
    type: Boolean,
    default: true
  }
}, {
  timestamps: true
});

const paymentSchema = new mongoose.Schema({
  userId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true
  },
  bookingId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Booking'
  },
  walletTransactionId: {
    type: mongoose.Schema.Types.ObjectId
  },
  amount: {
    type: Number,
    required: true,
    min: 0
  },
  currency: {
    type: String,
    default: 'USD',
    enum: ['USD', 'LBP', 'EUR']
  },
  paymentMethod: {
    type: String,
    enum: ['apple_pay', 'card', 'bank_transfer', 'wallet', 'cash'],
    required: true
  },
  paymentMethodDetails: paymentMethodSchema,
  status: {
    type: String,
    enum: ['pending', 'processing', 'completed', 'failed', 'cancelled', 'refunded'],
    default: 'pending'
  },
  paymentIntent: {
    // For Apple Pay and card payments
    intentId: String,
    clientSecret: String,
    paymentProvider: {
      type: String,
      enum: ['stripe', 'apple_pay', 'paypal', 'local_bank']
    }
  },
  applePayment: {
    // Apple Pay specific transaction data
    transactionIdentifier: String,
    paymentData: mongoose.Schema.Types.Mixed, // Encrypted payment data from Apple
    paymentMethod: {
      displayName: String,
      network: String, // Visa, Mastercard, etc.
      type: String // debit, credit
    },
    merchantSession: mongoose.Schema.Types.Mixed
  },
  processingFee: {
    type: Number,
    default: 0
  },
  netAmount: Number, // amount - processingFee
  description: String,
  metadata: mongoose.Schema.Types.Mixed,
  failureReason: String,
  processedAt: Date,
  refundedAt: Date,
  refundAmount: {
    type: Number,
    default: 0
  },
  // Commission tracking
  platformCommission: {
    rate: {
      type: Number,
      default: 0.05 // 5% default
    },
    amount: Number
  },
  providerEarnings: Number,
  // Receipt and invoice
  receiptNumber: {
    type: String,
    unique: true
  },
  invoiceUrl: String,
  receiptUrl: String
}, {
  timestamps: true
});

// Generate unique receipt number
paymentSchema.pre('save', async function(next) {
  if (this.isNew && !this.receiptNumber) {
    const timestamp = Date.now().toString();
    const random = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
    this.receiptNumber = `TGO-${timestamp}-${random}`;
  }

  // Calculate net amount and provider earnings
  if (this.amount && this.processingFee !== undefined) {
    this.netAmount = this.amount - this.processingFee;

    if (this.platformCommission.rate) {
      this.platformCommission.amount = this.netAmount * this.platformCommission.rate;
      this.providerEarnings = this.netAmount - this.platformCommission.amount;
    }
  }

  next();
});

// Indexes for efficient queries
paymentSchema.index({ userId: 1, createdAt: -1 });
paymentSchema.index({ bookingId: 1 });
paymentSchema.index({ status: 1 });
paymentSchema.index({ paymentMethod: 1 });
paymentSchema.index({ receiptNumber: 1 });
paymentSchema.index({ 'applePayment.transactionIdentifier': 1 });

// Instance methods
paymentSchema.methods.canBeRefunded = function() {
  const refundableStatuses = ['completed'];
  const maxRefundDays = 30;
  const daysSincePayment = (Date.now() - this.processedAt) / (1000 * 60 * 60 * 24);

  return refundableStatuses.includes(this.status) && 
         daysSincePayment <= maxRefundDays &&
         this.refundAmount < this.amount;
};

paymentSchema.methods.getRemainingRefundAmount = function() {
  return Math.max(0, this.amount - this.refundAmount);
};

// Static methods
paymentSchema.statics.findByReceiptNumber = function(receiptNumber) {
  return this.findOne({ receiptNumber });
};

paymentSchema.statics.getUserPaymentHistory = function(userId, page = 1, limit = 20) {
  const skip = (page - 1) * limit;
  return this.find({ userId })
    .populate('bookingId', 'serviceId date status')
    .sort({ createdAt: -1 })
    .skip(skip)
    .limit(limit);
};

module.exports = mongoose.model('Payment', paymentSchema);

server/models/Review.js.content:
const mongoose = require('mongoose');

const reviewSchema = new mongoose.Schema({
  serviceId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Service',
    required: true
  },
  bookingId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Booking',
    required: true
  },
  userId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true
  },
  providerId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true
  },
  rating: {
    type: Number,
    required: true,
    min: 1,
    max: 5
  },
  text: {
    type: String,
    required: true,
    trim: true,
    maxlength: 1000
  },
  text_ar: {
    type: String,
    trim: true,
    maxlength: 1000
  },
  images: [String], // URLs to review images
  isVerified: {
    type: Boolean,
    default: false // Verified if user actually used the service
  },
  isHidden: {
    type: Boolean,
    default: false // For admin moderation
  },
  helpfulCount: {
    type: Number,
    default: 0
  },
  reportCount: {
    type: Number,
    default: 0
  },
  sentiment: {
    type: String,
    enum: ['positive', 'neutral', 'negative'],
    default: 'neutral'
  },
  aiAnalysis: {
    keywords: [String],
    topics: [String],
    confidence: Number
  }
}, {
  timestamps: true
});

// Indexes
reviewSchema.index({ serviceId: 1, createdAt: -1 });
reviewSchema.index({ userId: 1 });
reviewSchema.index({ providerId: 1 });
reviewSchema.index({ rating: -1 });
reviewSchema.index({ isHidden: 1 });

// Ensure one review per booking
reviewSchema.index({ bookingId: 1 }, { unique: true });

// Update service rating after saving review
reviewSchema.post('save', async function() {
  const Service = mongoose.model('Service');
  const service = await Service.findById(this.serviceId);
  if (service) {
    await service.updateRating();
  }
});

// Update service rating after removing review
reviewSchema.post('remove', async function() {
  const Service = mongoose.model('Service');
  const service = await Service.findById(this.serviceId);
  if (service) {
    await service.updateRating();
  }
});

module.exports = mongoose.model('Review', reviewSchema);

server/models/Service.js.content:
const mongoose = require('mongoose');

const serviceSchema = new mongoose.Schema({
  title: {
    type: String,
    required: true,
    trim: true
  },
  title_ar: {
    type: String,
    trim: true
  },
  description: {
    type: String,
    required: true
  },
  description_ar: {
    type: String
  },
  category: {
    type: String,
    required: true,
    enum: [
      'maintenance', 'cleaning', 'delivery', 'events', 
      'care', 'gardens', 'auto', 'tech', 'admin'
    ]
  },
  subcategory: {
    type: String,
    required: true
  },
  price: {
    type: Number,
    required: true,
    min: 0
  },
  priceType: {
    type: String,
    enum: ['fixed', 'hourly', 'per_item'],
    default: 'fixed'
  },
  images: [{
    type: String // URLs to uploaded images
  }],
  location: {
    city: {
      type: String,
      required: true
    },
    area: String,
    coordinates: {
      type: [Number], // [longitude, latitude]
      index: '2dsphere'
    }
  },
  providerId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true
  },
  availability: [{
    date: Date,
    timeSlots: [String] // e.g., ['09:00', '10:00', '14:00']
  }],
  tags: [String],
  rating: {
    average: {
      type: Number,
      default: 0,
      min: 0,
      max: 5
    },
    count: {
      type: Number,
      default: 0
    }
  },
  status: {
    type: String,
    enum: ['pending', 'approved', 'rejected', 'inactive'],
    default: 'pending'
  },
  isActive: {
    type: Boolean,
    default: true
  },
  viewCount: {
    type: Number,
    default: 0
  },
  bookingCount: {
    type: Number,
    default: 0
  }
}, {
  timestamps: true
});

// Index for location-based searches
serviceSchema.index({ 'location.coordinates': '2dsphere' });
serviceSchema.index({ category: 1, status: 1 });
serviceSchema.index({ providerId: 1 });
serviceSchema.index({ rating: -1 });

// Update rating when reviews change
serviceSchema.methods.updateRating = async function() {
  const Review = mongoose.model('Review');
  const reviews = await Review.find({ serviceId: this._id });

  if (reviews.length === 0) {
    this.rating.average = 0;
    this.rating.count = 0;
  } else {
    const totalRating = reviews.reduce((sum, review) => sum + review.rating, 0);
    this.rating.average = totalRating / reviews.length;
    this.rating.count = reviews.length;
  }

  await this.save();
};

module.exports = mongoose.model('Service', serviceSchema);

server/models/User.js.content:
const mongoose = require('mongoose');
const bcrypt = require('bcryptjs');

const userSchema = new mongoose.Schema({
  name: {
    type: String,
    required: true,
    trim: true
  },
  email: {
    type: String,
    required: true,
    unique: true,
    lowercase: true,
    trim: true
  },
  phone: {
    type: String,
    required: true,
    unique: true
  },
  passwordHash: {
    type: String,
    required: true
  },
  role: {
    type: String,
    enum: ['client', 'provider', 'admin'],
    default: 'client'
  },
  language: {
    type: String,
    enum: ['en', 'ar'],
    default: 'en'
  },
  location: {
    city: String,
    coordinates: {
      type: [Number], // [longitude, latitude]
      index: '2dsphere'
    }
  },
  isBlocked: {
    type: Boolean,
    default: false
  },
  isVerified: {
    type: Boolean,
    default: false
  },
  isPremium: {
    type: Boolean,
    default: false
  },
  walletBalance: {
    type: Number,
    default: 0
  },
  avatar: String,
  verificationToken: String,
  resetPasswordToken: String,
  resetPasswordExpires: Date
}, {
  timestamps: true
});

// Hash password before saving
userSchema.pre('save', async function(next) {
  if (!this.isModified('passwordHash')) return next();

  try {
    const salt = await bcrypt.genSalt(10);
    this.passwordHash = await bcrypt.hash(this.passwordHash, salt);
    next();
  } catch (error) {
    next(error);
  }
});

// Compare password method
userSchema.methods.comparePassword = async function(candidatePassword) {
  return bcrypt.compare(candidatePassword, this.passwordHash);
};

// Hide sensitive data when converting to JSON
userSchema.methods.toJSON = function() {
  const user = this.toObject();
  delete user.passwordHash;
  delete user.verificationToken;
  delete user.resetPasswordToken;
  delete user.resetPasswordExpires;
  return user;
};

module.exports = mongoose.model('User', userSchema);

server/models/Wallet.js.content:
const mongoose = require('mongoose');

const transactionSchema = new mongoose.Schema({
  type: {
    type: String,
    enum: ['recharge', 'payment', 'refund', 'withdrawal', 'commission'],
    required: true
  },
  amount: {
    type: Number,
    required: true
  },
  description: String,
  relatedBookingId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Booking'
  },
  status: {
    type: String,
    enum: ['pending', 'completed', 'failed', 'cancelled'],
    default: 'pending'
  },
  paymentMethod: {
    type: String,
    enum: ['card', 'bank_transfer', 'cash', 'wallet']
  },
  externalTransactionId: String, // For payment gateway integration
  metadata: mongoose.Schema.Types.Mixed
}, {
  timestamps: true
});

const walletSchema = new mongoose.Schema({
  userId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true,
    unique: true
  },
  balance: {
    type: Number,
    default: 0,
    min: 0
  },
  pendingBalance: {
    type: Number,
    default: 0 // Money pending from completed services
  },
  totalEarnings: {
    type: Number,
    default: 0 // For providers
  },
  totalSpent: {
    type: Number,
    default: 0 // For clients
  },
  transactionHistory: [transactionSchema],
  lastTransactionAt: Date,
  refundStatus: {
    type: String,
    enum: ['none', 'pending', 'processed'],
    default: 'none'
  },
  paymentStatus: {
    type: String,
    enum: ['active', 'suspended', 'under_review'],
    default: 'active'
  },
  autoWithdraw: {
    enabled: {
      type: Boolean,
      default: false
    },
    threshold: {
      type: Number,
      default: 100
    },
    bankDetails: {
      accountNumber: String,
      bankName: String,
      accountHolderName: String
    }
  }
}, {
  timestamps: true
});

// Add transaction to wallet
walletSchema.methods.addTransaction = async function(transactionData) {
  const transaction = {
    ...transactionData,
    createdAt: new Date(),
    updatedAt: new Date()
  };

  this.transactionHistory.push(transaction);
  this.lastTransactionAt = new Date();

  // Update balance based on transaction type
  if (transaction.status === 'completed') {
    switch (transaction.type) {
      case 'recharge':
        this.balance += transaction.amount;
        break;
      case 'payment':
        this.balance -= transaction.amount;
        this.totalSpent += transaction.amount;
        break;
      case 'refund':
        this.balance += transaction.amount;
        break;
      case 'withdrawal':
        this.balance -= transaction.amount;
        break;
      case 'commission':
        this.balance += transaction.amount;
        this.totalEarnings += transaction.amount;
        break;
    }
  }

  await this.save();
  return transaction;
};

// Check if user has sufficient balance
walletSchema.methods.hasSufficientBalance = function(amount) {
  return this.balance >= amount;
};

// Get transaction history with pagination
walletSchema.methods.getTransactionHistory = function(page = 1, limit = 20) {
  const startIndex = (page - 1) * limit;
  const endIndex = startIndex + limit;

  return {
    transactions: this.transactionHistory
      .sort((a, b) => b.createdAt - a.createdAt)
      .slice(startIndex, endIndex),
    totalCount: this.transactionHistory.length,
    hasMore: endIndex < this.transactionHistory.length
  };
};

module.exports = mongoose.model('Wallet', walletSchema);
